// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "./NovaToken2.sol";

struct UserInfoV1 {
    uint256 lastStakedTime;
    uint256 amount; // How many LP tokens the user has provided.
    uint256 rewardDebt; // Reward debt. See explanation below.
    address next;
}

interface IMasterUniverseV1 {
    function emergencyWithdraw(uint256 _pid) external;

    function poolLength() external view returns (uint256);

    function userInfo(uint256 _pid, address _user)
        external
        view
        returns (UserInfoV1 memory);

    function startTimestamp() external view returns (uint256);
}

interface IMasterUniverseV2 {
    function deposit(uint256 _pid, uint256 _amount) external;

    function poolLength() external view returns (uint256);
}

contract NovaMigrationV2 {
    using SafeMath for uint256;

    IMasterUniverseV1 public immutable masterUniverseV1;
    IMasterUniverseV2 public immutable masterUniverseV2;

    IERC20 public novaV1;
    NovaToken2 public novaV2;
    uint256 public constant NOVA_REWARD = 1250;

    constructor(
        IMasterUniverseV1 _masterUniverseV1,
        IMasterUniverseV2 _masterUniverseV2,
        IERC20 _novaV1,
        NovaToken2 _novaV2
    ) {
        masterUniverseV1 = _masterUniverseV1;
        masterUniverseV2 = _masterUniverseV2;
        novaV1 = _novaV1;
        novaV2 = _novaV2;
    }

    function migrate() external {
        uint256 rewardEndTime = masterUniverseV1.startTimestamp() + 36 hours;
        uint256 _pid = 0;
        UserInfoV1 memory userInfoV1 = masterUniverseV1.userInfo(
            _pid,
            msg.sender
        );

        require(userInfoV1.amount > 0, "No deposit");

        uint256 pendingNova = userInfoV1
            .amount
            .mul(NOVA_REWARD)
            .mul(rewardEndTime.sub(userInfoV1.lastStakedTime))
            .div(36 hours)
            .div(945);

        address(masterUniverseV1).delegatecall(
            abi.encodeWithSignature("emergencyWithdraw(uint256 _pid)", _pid)
        );
        address(masterUniverseV2).delegatecall(
            abi.encodeWithSignature(
                "deposit(uint256 _pid, uint256 _amount)",
                _pid,
                userInfoV1.amount
            )
        );

        novaV2.mint(msg.sender, pendingNova);
    }

    function swap(uint256 _amount) external {
        novaV1.transferFrom(msg.sender, address(novaV1), _amount);
        novaV2.mint(msg.sender, _amount);
    }
}
