// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract NovaToken2 is ERC20("NOVA", "NOVA"), Ownable {
    address public migrator;
    bool public migratorDisabled;

    modifier onlyMinter() {
        require(
            owner() == _msgSender() ||
                (migratorDisabled == false && migrator == _msgSender()),
            "caller is not the minter"
        );
        _;
    }

    /// @notice Creates `_amount` token to `_to`. Must only be called by the owner (MasterUniverse).
    function mint(address _to, uint256 _amount) public onlyMinter {
        _mint(_to, _amount);
    }

    function setMigrator(address _migrator) public onlyOwner {
        require(migratorDisabled == false, "migrator disabled");
        migrator = _migrator;
    }

    function disableMigrator() public onlyOwner {
        migrator == address(0);
        migratorDisabled = true;
    }
}
